    const PriceForStripe = price * 100
    const PublishableKey = "pk_test_51H4TcXKK0gugLsixM4c4qLYjnQwBfNCWsAR00gvt5cbVlKlq2MFfVaMKL5wGhK2r6FfLLF1KDt0HxwgArL8XPB4200qdnkBYD3"

