import React, { Fragment } from 'react';
import { withRouter } from "react-router-dom"
import CollectionItem from "../collection-item/collection-item.jsx"

import "./collection-preview.scss"

import toggleCartHidden from "../../redux/cart/cart.actions/toggleCartHidden.js"
import { connect } from 'react-redux';

import onClickFunction from "../testFunction.js"


const CollectionPreview = ({ title, items, history, toggleCartHidden }) => {



    return (
        <Fragment>
            <div className="collection-preview">
                <h1 onClick={() => onClickFunction()}
                    className='title'>{title.toUpperCase()}</h1>
                <div className='preview'>
                    {items
                        .filter((item, idx) => idx < 4)
                        .map(item => (
                            <CollectionItem key={item.id} item={item} />
                        ))}
                </div>
            </div>
        </Fragment>
    )
}

const mapDispatchToProps = (dispatch) => ({
    toggleCartHidden: () => dispatch(toggleCartHidden())
})


export default withRouter(connect(null, mapDispatchToProps)(CollectionPreview));
