import CartActionTypes from "./cart-types.js"

const INITIAL_STATE = {
    hidden: true
}

const cartReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case CartActionTypes.TOOGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: action.payload
            }

        default:
            return state
    }
}

export default cartReducer;
