import React, { Fragment, useCallback } from "react"
import { connect, useDispatch } from "react-redux"
import { createStructuredSelector } from "reselect"

import CollectionPreview from "../collection-preview/collection-preview.jsx"

import selectCollectionsForPreview from "../../redux/shop/selectors/for-preview.selector.js"

import toggleCartHidden from "../../redux/cart/cart.actions/toggleCartHidden"
import CartActionTypes from "../../redux/cart/cart-types.js"


import "./collections-overview.scss"

const CollectionsOverview = (collections) => {
    const dispatch = useDispatch()
    const incrementCartDropDown = useCallback(
        () => dispatch({ type: CartActionTypes.TOGGLE_CART_HIDDEN })
    )

    return (
        <Fragment>
            <button onClick={incrementCartDropDown}>Toggle cart dropdown</button>
            <div className="collections-overview" >
                {
                    collections.collections.map(({ id, ...otherCollectionProps }) => (
                        <CollectionPreview
                            key={id}
                            {...otherCollectionProps}
                        />
                    ))
                }
            </div>
        </Fragment>
    )
}


// 



const mapStateToProps = createStructuredSelector({
    collections: selectCollectionsForPreview
})

export default connect(mapStateToProps)(CollectionsOverview);
