import ShopActionsTypes from "./shop.types.js"

import SHOP_DATA from "./shop.data.js"

import CartActionTypes from "../cart/cart-types.js"

const INITIAL_STATE = {
    state: null,
    isFetching: false,
    errorMessage: undefined
}

const shopReducer = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case CartActionTypes.TOGGLE_CART_HIDDEN:
            return {
                ...state,
                state: SHOP_DATA
            }


        case ShopActionsTypes.FETCH_COLLECTIONS_START:
            return {
                ...state,
                state: null,
                isFetching: true
            }

        case ShopActionsTypes.FETCH_COLLECTIONS_SUCCESS:
            return {
                ...state,
                isFetching: false,
                state: action.payload
            }

        case ShopActionsTypes.FETCH_COLLECTIONS_FAILURE:
            return {
                ...state,
                isFetching: false,
                errorMessage: action.payload
            }


        default:
            return state
    }
}

export default shopReducer;
