    render() {
        const { match } = this.props
        return (
            <Fragment>
                <div className="shop-page" >
                    <Route exact path={`${match.path}`} component={CollectionsOverview} />
                    <Route exact path={`${match.path}/:categoryId`} component={CollectionPage} />
                </div>
            </Fragment>
        )
    }
}

// const mapDispatchToProps = (dispatch) => ({
